import copy

INPUT = ""


def find_position_after_unique(substring_length, input_val):
    for i in range(len(input_val)):
        sub_string = input_val[i:(i+substring_length)]
        if len(set(sub_string)) == substring_length:
            return i + substring_length


if __name__ == '__main__':
    print(find_position_after_unique(4, INPUT))
    print(find_position_after_unique(14, INPUT))
